import axios from 'axios'
import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Container, Table, Alert, Form, FormGroup, Label, Input, Button } from "reactstrap"

import { api } from '../../../config'

export const ListarItensCompra = () => {

    let [alo, setAlo] = useState()
    let [cadAtivado, setCadAtivado] = useState()
    let [editAtivado, setEditAtivado] = useState()
    let [idEdit, setIdEdit] = useState()
    const [data, setData] = useState([])
    const [status, setStatus] = useState({
        type: '',
        message: ''
    })
    const [item, setItem] = useState({
        CompraId: '',
        ProdutoId: '',
        quantidade: '',
        valor: ''
    })

    let [quantidade, setQuantidade] = useState()
    let [valor, setValor] = useState()

    const valorInput = e => setItem({
        ...item, [e.target.name]: e.target.value
    })

    const cadOn = (alerto) => {
        if (cadAtivado !== true) {
            setCadAtivado(cadAtivado = true)
        } else {
            setCadAtivado(cadAtivado = false)
            setAlo(alo = alerto)
        }
    }

    const getItens = async () => {
        await axios.get(api + '/listaprodutos').then(
            (response) => {
                setData(response.data)

            }
        ).catch(() => {
            setStatus({
                type: 'error',
                message: "Erro: sem conexão com a API."
            })
            console.log("Erro: sem conexão com a API.")
        })
    }


    const getDataItem = async (id) => {
        await axios.get(`${api}/produto/${id}`).then(
            (response) => {

                setQuantidade(response.data.quantidade)
                setValor(response.data.valor)

            }
        ).catch(() => {
            setStatus({
                type: 'error',
                message: "Erro: sem conexão com a API."
            })
            console.log("Erro: sem conexão com a API.")
        })
    }


    const delItem = async (idItem) => {
        console.log(idItem)
        const headers = {
            'Content-type': 'application/json'
        }

        if (window.confirm('Tem certeza que quer excluir esse item?')) {
            console.log('confirmado')
            await axios.delete(`${api}/excluir-produto/${idProduto}`, { headers })
                .then((response) => {
                    setStatus({
                        type: 'error',
                        message: response.data.message
                    })
                    getItens()
                    setAlo(true)
                }).catch(() => {
                    setStatus({
                        type: 'error',
                        message: 'Erro: Não foi possível conexão com API'
                    })
                })
        }
    }

    const cadItem = async e => {
        e.preventDefault()

        const headers = {
            'Content-Type': 'application/json'
        }

        await axios.post(`${api}/novoprodutos`, item, { headers })
            .then((response) => {
                if (response.data.error) {
                    setStatus({
                        type: 'error',
                        message: response.data.message
                    })
                    console.log(status.message)
                } else {
                    setStatus({
                        type: 'success',
                        message: response.data.message
                    })
                }
            })
            .catch(() => {
                console.log('Erro! Sem conexão.')
            })

        console.log(status.type)
        getItens()
        cadOn(true)
        setItem({
            ProdutoId: '',
            CompraId: '',
            quantidade: '',
            valor: '',
        })
    }

    editAtivado = false

    const editOn = (editId) => {
        getEData(editId)
        // getDataCompra(editId)
        setEditAtivado(editAtivado = true)
        setIdEdit(idEdit = editId)
        // setDaco(comData)
        console.log(editAtivado)
        console.log(quantidade)
        console.log(valor)
        // console.log(editId)
        // console.log(daCo)
    }

    const editOff = ()=>{
        setEditAtivado(false)
        setIdEdit(0)
    }

    const getEData = async (id) => {
        await axios(`${api}/produto/${id}`)
            .then((response) => {
                setQuantidade(response.data.produtos.quantidade)
                setValor(response.data.produtos.valor)
            })
            .catch(() => {
                console.log("Erro: não foi possível acessar API.")
            })
        setIdEdit(id)
        console.log(idEdit)
    }

    const editProduto = async (e, id, quantidade, valor) => {
        e.preventDefault()
        setEditAtivado(false)
        setIdEdit(0)

        const headers = {
            'Content-Type': "application/json"
        }

        await axios.put(`${api}/editar-produto/${id}`, { quantidade, valor }, { headers }).then(response => {
            setStatus({
                type: 'success',
                message: 'Alteração feita com sucesso.'
            })
            // console.log(response.data.type)
            console.log(response.data.message)
        }).catch(() => {
            setStatus({
                type: 'error',
                message: 'Não foi possível acessar API.'
            })
        })
    }

    const alerta = () => {
        let alerto = false
        cadOn(alerto)
    }

    useEffect(() => {
        getItens();
        getDataItem(idEdit)
    }, [idEdit])

    return (
        <div>
            <Container>
                <div>
                    <div>
                        <h1>Visualizar informações dos itens</h1>
                    </div>
                    <div className="m-auto p-2">
                        <span onClick={() => alerta()}
                            className="btn btn-outline-primary btn-sm">Cadastrar</span>


                        {cadAtivado === true ?
                            <Form className="d-flex flex-row mt-2 bg-light border-top border-primary" onSubmit={cadItem}>
                                <FormGroup className='d-flex flex-row'>

                                    <FormGroup className="w-70 p-2">
                                        <Label>
                                            Quantidade
                                        </Label>
                                        <Input
                                            name="quantidade"
                                            placeholder="Quantidade"
                                            type="text"
                                            onChange={valorInput}
                                            required
                                        />
                                    </FormGroup>
                                    <FormGroup className="w-70 p-2">
                                        <Label>
                                            Valor do produto
                                        </Label>
                                        <Input
                                            name="valor"
                                            placeholder="Valor do produto"
                                            type="text"
                                            onChange={valorInput}
                                            required
                                        />
                                    </FormGroup>
                                </FormGroup>
                                <FormGroup className="h-50 d-flex my-auto mb-auto">
                                    <Button type="submit" outline color="primary">
                                        Cadastrar
                                    </Button>
                                    <Button type="reset" outline color="primary">
                                        Limpar
                                    </Button>
                                </FormGroup>
                            </Form> : ""}
                    </div>
                    {status.type === 'error' && cadAtivado !== true && alo === true ?
                        <Alert color="danger">
                            {status.message}
                        </Alert>
                        : ''}
                    {status.type === 'success' && cadAtivado !== true && alo === true ?
                        <Alert color="success">
                            {status.message}
                        </Alert> : ''}
                </div>
                <Table striped>
                    <thead>
                        <tr>
                            <th>Id da compra</th>
                            <th>Id do produto</th>
                            <th>Quantidade</th>
                            <th>Valor</th>
                            <th>Ação</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(item => (
                            <tr key={item.CompraId}>
                                <td>{item.CompraId}</td>
                                <td>{item.ProdutoId}</td>

                                {item.id !== idEdit ?
                                    <td>{item.quantidade}</td> :
                                    <td>
                                        <Form className="w-70 p-2">
                                            <Input
                                                name="quantidade"
                                                placeholder="Quantidade"
                                                type="text"
                                                value={quantidade}
                                                onChange={e => setQuantidade(e.target.value)}
                                            />
                                        </Form>
                                    </td>
                                }

                                {item.id !== idEdit ?
                                    <td>{item.valor}</td> :
                                    <td>
                                        <Form className="w-70 p-2">
                                            <Input
                                                name="valor"
                                                placeholder="Valor"
                                                type="text"
                                                value={valor}
                                                onChange={e => setValor(e.target.value)}
                                            />
                                        </Form>
                                    </td>
                                }
                                {item.id !== idEdit && editAtivado !== true ?
                                    <td className="text-center">
                                        <Link to={"/itens-produto/" + item.id}
                                            className="btn btn-outline-primary btn-sm">
                                            Itens
                                        </Link>
                                        <span onClick={() => editOn(item.CompraId, item.ProdutoId)}
                                            className="btn btn-outline-warning btn-sm">
                                            Editar
                                        </span>

                                        <span className="btn btn-outline-danger btn-sm"
                                            onClick={() => delProduto(item.CompraId, item.ProdutoId)}>Excluir</span>
                                    </td>
                                    :
                                    <td className="text-center">
                                        <span onClick={e => editProduto(e, item.CompraId, item.ProdutoId, nome, descricao)}
                                            className="btn btn-outline-success btn-sm">
                                            Confirmar
                                        </span>

                                        <span onClick={e => editOff()}
                                            className="btn btn-outline-danger btn-sm">
                                            Cancelar
                                        </span>
                                    </td>}
                            </tr>

                        ))}

                    </tbody>
                </Table>
            </Container>
        </div>
    )
}
