import axios from "axios"
// import { useState } from "react/cjs/react.development"
import { api } from "../config"

export const getCliente = async (id, data, status, tO) =>{
    await axios.get(`${api}/cliente/${id}`)
    .then((response) =>{
        data(response.data.cliente)
    }).catch(()=>{
        status({
            type:'error',
            message:'Não foi possível encontrar o cliente.'
        })
    })
    toggling(id, tO)
}

export let oldId

export const toggling = async (id, toggleOn)=>{
    if(toggleOn === false){
        toggleOn = true
    }else if(oldId === id){
        toggleOn = false
    }else {oldId = id}
}
