import axios from "axios"
import { useEffect, useState } from "react"
import { Link } from "react-router-dom"
import { Alert, Button, Container, Form, FormGroup, Input, Label } from "reactstrap"
import { api } from "../../../config"

export const EditarItemP = (props) => {

    const [PedidoId, setPedidoId] = useState(props.match.params.PedidoId)
    const [ServicoId, setServicoId] = useState(props.match.params.ServicoId)
    const [quantidade, setQuantidade] = useState('')
    const [valor, setValor] = useState('')

    const [status, setStatus] = useState({
        type:'',
        message:''
    })

    const editItemPedido = async e =>{
        e.preventDefault()

        const headers = {
            'Content-Type':"application/json"
        }

        await axios.put(`${api}/item-pedido/${ServicoId}/${PedidoId}`,{
            quantidade, valor
        }, {headers}).then(response=>{
            setStatus({
                type:'success',
                message:'Alteração feita com sucesso.'
            })
            console.log(response.data.type)
            console.log(response.data.message)
        }).catch(()=>{
            setStatus({
                type:'error',
                message:'Não foi possível acessar API.'
            })
        })
    }

    useEffect(()=>{
        const getItemPedido = async ()=>{
            await axios(`${api}/item-pedido/${ServicoId}/${PedidoId}`)
            .then((response)=>{
                setServicoId(response.data.item.ServicoId)
                setPedidoId(response.data.item.PedidoId)
                setQuantidade(response.data.item.quantidade)
                setValor(response.data.item.valor)
            })
            .catch(()=>{
                console.log("Erro: não foi possível acessar API.")
            })
        }
        getItemPedido()
    },[ServicoId,PedidoId])

    return (
        <Container>
            <div className="d-flex">
                <div className="m-auto p-2">
                    <h1>Editar item pedido</h1>
                </div>
                <div className="p-2">
                    <Link to='/listar-itens-pedidos'
                        className="btn btn-outline-success btn-sm">
                        Itens pedidos</Link>
                </div>
            </div>
            <hr className="m-1" />

            {status.type === 'error' ?
                <Alert color="danger">
                    {status.message}
                </Alert> : ''}
            {status.type === 'success' ?
                <Alert color="success">
                    {status.message}
                </Alert> : ''}

            <Form className="p-2" onSubmit={editItemPedido}>

                <FormGroup className="p-2">
                    <Label>
                        Quantidade
                    </Label>
                    <Input
                        name="quantidade"
                        placeholder="Quantidade"
                        type="text"
                        value={quantidade}
                        onChange={e => setValor(e.target.value)}
                    />
                </FormGroup>

                <FormGroup className="p-2">
                    <Label>
                        Valor
                    </Label>
                    <Input
                        name="valor"
                        placeholder="Valor"
                        type="text"
                        value={valor}
                        onChange={e => setValor(e.target.value)}
                    />
                </FormGroup>

                <FormGroup className="d-flex">
                    <Button type="submit" outline color="warning">
                        Alterar
                    </Button>
                    <Button type="reset" outline color="primary">
                        Limpar
                    </Button>
                </FormGroup>
            </Form>
        </Container>
    )
}
