import axios from 'axios'
import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Container, Table, Alert, Form, FormGroup, Label, Input, Button } from "reactstrap"

import { api } from '../../../config'

export const ListarCompras = () => {

    let [alo, setAlo] = useState()
    let [cadAtivado, setCadAtivado] = useState()
    let [editAtivado, setEditAtivado] = useState()
    let [idEdit, setIdEdit]=useState()
    const [data, setData] = useState([])
    const [status, setStatus] = useState({
        type: '',
        message: ''
    })
    const [compra, setCompra] = useState({
        id: '',
        ClienteId: '',
        data: ''
    })

    const [dataCompra, setDataCompra] = useState()
    let daCo 

    const valorInput = e => setCompra({
        ...compra, [e.target.name]: e.target.value
    })

    const cadOn = (alerto) => {
        if (cadAtivado !== true) {
            setCadAtivado(cadAtivado = true)
        } else {
            setCadAtivado(cadAtivado = false)
            setAlo(alo = alerto)
        }
    }

    const getCompras = async () => {
        await axios.get(api + '/listacompras').then(
            (response) => {
                setData(response.data)
            }
        ).catch(() => {
            setStatus({
                type: 'error',
                message: "Erro: sem conexão com a API."
            })
            // console.log("Erro: sem conexão com a API.")
        })
    }

    const delCompra = async (idCompra) => {
        console.log(idCompra)

        const headers = {
            'Content-type': 'application/json'
        }

        await axios.delete(`${api}/excluir-compra/${idCompra}`, { headers })
            .then((response) => {
                // console.log(response.data.type)
                // console.log(response.data.message)
                setStatus({
                    type: 'error',
                    message: response.data.message
                })
                getCompras()
                setAlo(true)
            }).catch(() => {
                setStatus({
                    type: 'error',
                    message: 'Erro: Não foi possível conexão com API'
                })
            })
    }

    const cadCompra = async e => {
        e.preventDefault()

        const headers = {
            'Content-Type': 'application/json'
        }

        await axios.post(`${api}/novacompras`, compra, { headers })
            .then((response) => {
                if (response.data.error) {
                    setStatus({
                        type: 'error',
                        message: response.data.message
                    })
                    console.log(status.message)
                } else {
                    setStatus({
                        type: 'success',
                        message: response.data.message
                    })
                }
            })
            .catch(() => {
                console.log('Erro! Sem conexão.')
            })

        console.log(status.type)
        getCompras()
        cadOn(true)
        setCompra({
            id: '',
            ClienteId: '',
            data: ''
        })
    }
    editAtivado = false
    const editOn = (editId, comData) => {
        setEditAtivado(editAtivado = true)
        setIdEdit(idEdit = editId)
        daCo = comData
        console.log(editId)
    }

    const editCompra = async e =>{
        e.preventDefault()

        const headers = {
            'Content-Type':"application/json"
        }

        await axios.put(`${api}/editar-compra/`, dataCompra,{

        }, {headers}).then(response=>{
            setStatus({
                type:'success',
                message:'Alteração feita com sucesso.'
            })
            // console.log(response.data.type)
            // console.log(response.data.message)
        }).catch(()=>{
            setStatus({
                type:'error',
                message:'Não foi possível acessar API.'
            })
        })
    }

    const alerta = () => {
        let alerto = false
        cadOn(alerto)
    }

    useEffect(() => {
        getCompras();
    }, [])

    return (
        <div>
            <Container>
                <div>
                    <div>
                        <h1>Visualizar informações das compras</h1>
                    </div>
                    <div className="m-auto p-2">
                        <span onClick={() => alerta()}
                            className="btn btn-outline-primary btn-sm">Cadastrar</span>


                        {cadAtivado === true ?
                            <Form className="d-flex flex-row mt-2 bg-light border-top border-primary" onSubmit={cadCompra}>
                                <FormGroup className='d-flex flex-row'>
                                    <FormGroup className="w-70 p-2">
                                        <Label>
                                            Id do Cliente
                                        </Label>
                                        <Input
                                            name="ClienteId"
                                            placeholder="Id do cliente"
                                            type="text"
                                            onChange={valorInput}
                                            required
                                        />
                                    </FormGroup>
                                    <FormGroup className="w-70 p-2">
                                        <Label>
                                            Data da Compra
                                        </Label>
                                        <Input
                                            name="data"
                                            placeholder="Data da compra"
                                            type="text"
                                            onChange={valorInput}
                                            required
                                        />
                                    </FormGroup>
                                </FormGroup>
                                <FormGroup className="h-50 d-flex my-auto mb-auto">
                                    <Button type="submit" outline color="primary">
                                        Cadastrar
                                    </Button>
                                    <Button type="reset" outline color="primary">
                                        Limpar
                                    </Button>
                                </FormGroup>
                            </Form> : ""}
                    </div>
                    {status.type === 'error' && cadAtivado !== true && alo === true?
                        <Alert color="danger">
                            {status.message}
                        </Alert>
                        : ''}
                    {status.type === 'success' && cadAtivado !== true && alo === true?
                        <Alert color="success">
                            {status.message}
                        </Alert> : ''}
                </div>
                <Table striped>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Id do Cliente</th>
                            <th>Data</th>
                            <th>Ação</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(item => (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.ClienteId}</td>
                                {editAtivado !== true && item.id !== idEdit?<td>{item.data}</td>: <td>
                                    <Form><Input
                                                name="data"
                                                placeholder="Data da compra"
                                                type="text"
                                                value={dataCompra}
                                                onChange={e => setDataCompra(e.target.value)}

                                            /></Form>
                                </td> }
                                <td className="text-center">
                                    <Link to={"/itens-compra/" + item.ClienteId}
                                        className="btn btn-outline-primary btn-sm">
                                        Itens
                                    </Link>
                                    <span onClick={() => editOn(item.id, item.data)}
                                        className="btn btn-outline-warning btn-sm">
                                        Editar
                                    </span>

                                    <span className="btn btn-outline-danger btn-sm"
                                        onClick={() => delCompra(item.id)}>Excluir</span>
                                </td>
                            </tr>

                            ))}

                    </tbody>
                </Table>
            </Container>
        </div>
    )
}
