import axios from 'axios'
import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Container, Table, Alert, Form, FormGroup, Label, Input, Button } from "reactstrap"

import { api } from '../../../config'

export const ListarCompras = () => {

    let [cadAtivado, setCadAtivado] = useState()
    const [data, setData] = useState([])
    const [status, setStatus] = useState({
        type: '',
        message: ''
    })
    const [compra, setCompra] = useState({
        id: '',
        ClienteId: '',
        data: ''
    })

    const valorInput = e => setCompra({
        ...compra, [e.target.name]: e.target.value
    })

    const cadOn = () => {
        if (cadAtivado !== true) {
            setCadAtivado(cadAtivado = true)
        } else {
            setCadAtivado(cadAtivado = false)
        }
    }

    const getCompras = async () => {
        await axios.get(api + '/listacompras').then(
            (response) => {
                setData(response.data)
            }
        ).catch(() => {
            setStatus({
                type: 'error',
                message: "Erro: sem conexão com a API."
            })
            // console.log("Erro: sem conexão com a API.")
        })
    }

    const delCompra = async (idCompra) => {
        console.log(idCompra)

        const headers = {
            'Content-type': 'application/json'
        }

        await axios.delete(`${api}/excluir-compra/${idCompra}`, { headers })
            .then((response) => {
                console.log(response.data.type)
                console.log(response.data.message)
                getCompras()
            }).catch(() => {
                setStatus({
                    type: 'error',
                    message: 'Erro: Não foi possível conexão com API'
                })
            })
    }

    const cadCompra = async e => {
        e.preventDefault()

        const headers = {
            'Content-Type': 'application/json'
        }

        await axios.post(`${api}/novacompras`, compra, { headers })
            .then((response) => {
                if (response.data.error) {
                    setStatus({
                        type: 'error',
                        message: response.data.message
                    })
                    console.log(status.message)
                } else {
                    setStatus({
                        type: 'success',
                        message: response.data.message
                    } )
                }
            })
            .catch(() => {
                console.log('Erro! Sem conexão.')
            })

        console.log(status.type)
        getCompras()
        cadOn()
        setCompra({
            id: '',
            ClienteId: '',
            data: ''
        })
    }
    useEffect(() => {
        getCompras();
    }, [])

    return (
        <div>
            <Container>
                <div>
                    <div>
                        <h1>Visualizar informações das compras</h1>
                    </div>

                    <div className="m-auto p-2">
                        <span onClick={() => cadOn()}
                            className="btn btn-outline-primary btn-sm">Cadastrar</span>
                        {cadAtivado === true ?
                            <Form className="p-2" onSubmit={cadCompra}>
                                <FormGroup className="p-2">
                                    <Label>
                                        Id do Cliente
                                    </Label>
                                    <Input
                                        name="ClienteId"
                                        placeholder="Id do cliente"
                                        type="text"
                                        onChange={valorInput}
                                        required
                                    />
                                </FormGroup>
                                <FormGroup className="p-2">
                                    <Label>
                                        Data da Compra
                                    </Label>
                                    <Input
                                        name="data"
                                        placeholder="Data da compra"
                                        type="text"
                                        onChange={valorInput}
                                        required
                                    />
                                </FormGroup>
                                <FormGroup className="d-flex">
                                    <Button type="submit" outline color="primary">
                                        Cadastrar
                                    </Button>
                                    <Button type="reset" outline color="primary">
                                        Limpar
                                    </Button>
                                </FormGroup>
                            </Form> : ""}
                    </div>
                    {status.type === 'error' && cadAtivado !== true?
                <Alert color="danger">
                    {status.message}
                </Alert> : ''}
            {status.type === 'success' && cadAtivado !== true?
                <Alert color="success">
                    {status.message}
                </Alert> : ''}
                </div>
                <Table striped>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Id do Cliente</th>
                            <th>Data</th>
                            <th>Ação</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(item => (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.ClienteId}</td>
                                <td>{item.data}</td>
                                <td className="text-center">
                                    <Link to={"/pedidos-servico/" + item.id}
                                        className="btn btn-outline-primary btn-sm">
                                        Pedidos
                                    </Link>
                                    <span className="btn btn-outline-danger btn-sm"
                                        onClick={() => delCompra(item.id)}>Excluir</span>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </Container>
        </div>
    )
}
