import axios from 'axios'
import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Container, Table, Alert } from "reactstrap"

import { api } from '../../../config'

export const ListarCompras = () => {

    const [data, setData] = useState([])
    const [status, setStatus] = useState({
        type: '',
        message: ''
    })

    const getCompras = async () => {
        await axios.get(api + '/listacompras').then(
            (response) => {
                setData(response.data)
            }
        ).catch(() => {
            setStatus({
                type: 'error',
                message: "Erro: sem conexão com a API."
            })
            // console.log("Erro: sem conexão com a API.")
        })
    }

    const delCompra = async (idCompra) =>{
        console.log(idCompra)

        const headers = {
            'Content-type':'application/json'
        }

        await axios.delete(`${api}/excluir-compra/${idCompra}`, {headers})
        .then((response)=>{
            console.log(response.data.type)
            console.log(response.data.message)
            getCompras()
        }).catch(()=>{
            setStatus({
                type:'error',
                message:'Erro: Não foi possível conexão com API'
            })
        })
    }

    useEffect(() => {
        getCompras();
    }, [])

    return (
        <div>
            <Container>
                <div>
                    <div>
                        <h1>Visualizar informações das compras</h1>
                    </div>
                    <div className="m-auto p-2">
                        <Link to="/cadastrar-servicos"
                            className="btn btn-outline-primary btn-sm">Cadastrar</Link>
                            {status.type === 'error' ?
                        <Form className="p-2" onSubmit={cadCliente}>
                        <FormGroup className="p-2">
                            <Label>
                                Nome
                            </Label>
                            <Input
                                name="nome"
                                placeholder="Nome"
                                type="text"
                                onChange={valorInput}
                            />
                        </FormGroup></Form> : ""}
                    </div>
                    {status.type === 'error' ?
                        <Alert color="danger">
                            {status.message}
                        </Alert> : ""}
                </div>
                <Table striped>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Id do Cliente</th>
                            <th>Data</th>
                            <th>Ação</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(item => (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.ClienteId}</td>
                                <td>{item.data}</td>
                                <td className="text-center">
                                    <Link to={"/pedidos-servico/" + item.id}
                                        className="btn btn-outline-primary btn-sm">
                                        Pedidos
                                    </Link>
                                    <span className="btn btn-outline-danger btn-sm"
                                    onClick={()=>delCompra(item.id)}>Excluir</span>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </Container>
        </div>
    )
}
